mapper.select:
select e.id, e.e_userid as eUserid, e.e_person_shop as ePersonShop, e.e_person_shop_id as ePersonShopId, e.e_content as eContent, e.create_time as createTime,e.score,e.picture,e.projects_id as projectsId, p.real_name as realName,d.dept_name as deptName from t_evaluate e left join t_personage p on e.e_userid = p.id left join t_dept d on e.e_person_shop_id=d.id
and e.e_person_shop_id = #{ePersonShopId,jdbcType=VARCHAR}
order by e.create_time desc

select e.id,e.e_userid as eUserid,e.e_content as eContent,e.picture ,e.anonymity, e.create_time as createTime,e.score,p.user_img as userimg,s.p_name as pName from t_evaluate e left join t_personage p on e.e_userid=e.e_userid left join t_projects s on e.projects_id=s.id
and e.e_person_shop_id = #{ePersonShopId,jdbcType=VARCHAR}
and e.e_userid = #{eUserid,jdbcType=VARCHAR}
order by e.create_time desc

mapper.insert:
insert into t_evaluate (id, e_userid, e_person_shop, 
  e_person_shop_id, e_content, create_time,projects_id
  )
values (#{id,jdbcType=VARCHAR}, #{eUserid,jdbcType=VARCHAR}, #{ePersonShop,jdbcType=VARCHAR}, 
  #{ePersonShopId,jdbcType=VARCHAR}, #{eContent,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  , #{projectsId,jdbcType=VARCHAR}
  )
