mapper.select:
select i.id, i.i_time as iTime, i.i_code as iCode, i.i_mode as iMode, i.openid, p.p_name as pName,u.head_portrait as headPortrait,d.dept_name as deptName, u.user_name as userName ,d.site, i.i_price as iPrice from t_indent i left join t_projects p on i.i_type=p.id left join t_user u on i.hair_id=u.user_id left join t_dept d on i.dept_id=d.id
and i.openid= #{openid,jdbcType=VARCHAR}
and i.i_condition= #{iCondition,jdbcType=VARCHAR}
and i.id= #{id,jdbcType=VARCHAR}

select i.id, i.i_time as iTime, i.i_code as iCode, i.i_mode as iMode, i.openid, p.p_name as pName,u.head_portrait as headPortrait,d.dept_name as deptName, u.user_name as userName ,d.site,pg.real_name as realName,pg.user_img as userImg from t_indent i left join t_projects p on i.i_type=p.id left join t_user u on i.hair_id=u.user_id left join t_dept d on i.dept_id=d.id left join t_personage pg on pg.id=i.openid
and i.hair_id= #{hairId,jdbcType=VARCHAR}
and i.i_condition in ('1','2')
and i.i_condition = #{iCondition,jdbcType=VARCHAR}

select i.id,i.i_code as iCode ,i.i_phone as iPhone,i.hair_id as haiId,i.i_price as iPrice, i.i_time as iTime,i.i_condition as iCondition, p.real_name as realName,u.user_name as userName, d.dept_name as deptName, ps.p_name as pName from t_indent i left join t_personage p on i.openid = p.id left join t_user u on i.hair_id=u.user_id left join t_dept d on i.dept_id = d.id left join t_projects ps on i.i_type = ps.id
and hair_id= #{hairId,jdbcType=VARCHAR}
and i_condition= #{iCondition,jdbcType=VARCHAR}
and openid= #{openid,jdbcType=VARCHAR}
and i_condition not in ('5','6','7')
order by is_time desc

select
round(sum(i_price),2) as price
from t_indent


    and i_time >= #{startTime,jdbcType=VARCHAR}


    and i_time <= #{entTime,jdbcType=VARCHAR}


    and i_mode= #{mode,jdbcType=VARCHAR}


    and dept_id in

      '${deptId}'
